package Coding;

public class Arthematic_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=120, b=70;
		
		System.out.println("a + b = "+(a+b));
		System.out.println("a - b = "+(a-b));
		System.out.println("a * b = "+(a*b));
		System.out.println("a / b = "+(a/b));
		
		System.out.print("a + b = "+(a+b));
		System.out.print("a - b = "+(a-b));
		System.out.print("a * b = "+(a*b));
		System.out.print("a / b = "+(a/b));
		
		System.out.printf("a + b = "+(a+b));
		System.out.printf("a - b = "+(a-b));
		System.out.printf("a * b = "+(a*b));
		System.out.printf("a / b = "+(a/b));
		

	}

}
